create database Convalidaciones
GO

use Convalidaciones
GO

create table Alumno(
no_control char(8) primary key,
nombre char(50) not null,
apellidoP char(50) not null,
apellidoM char (50),
semestre smallint not null,
carrera char(80) not null
)

create table Materias(
no_materia char(4) primary key,
clave_materia char (8),
nombre char (80) not null,
carrera char (80) not null,
creditos smallint not null,
)

create table Cursa(
numero_control char (8) Foreign key references Alumno,
clave_materia char (4) Foreign key references Materias,
calificacion float not null,
primary key (numero_control, clave_materia) 
)

create table Convalidaciones(
clave_materia_cursada char (4) Foreign key references Materias,
clave_materia_convalidar char (4) Foreign key references Materias,
no_control char (8) Foreign key references Alumno,
primary key (no_control, clave_materia_cursada, clave_materia_convalidar) 
)
GO


